<?phpif(isset($_GET['backup']) && $_GET['backup']=='true')	//backup_tables('localhost','root','','wp'); $host,$user,$pass,$name,$table = 'wp_kushmicronews'	backup_tables(DB_HOST, DB_USER, DB_PASSWORD, DB_NAME);/* backup the db OR just a table */function backup_tables($host,$user,$pass,$name){global $wpdb;	$table = $wpdb->prefix . "kushmicronews";		$link = mysql_connect($host,$user,$pass);	mysql_select_db($name,$link);				$result = mysql_query('SELECT * FROM '.$table);		$num_fields = mysql_num_fields($result);				$return = "";				$return.= 'DROP TABLE IF EXISTS '.$table.';';		$row2 = mysql_fetch_row(mysql_query('SHOW CREATE TABLE '.$table));		$return.= "\n\n".$row2[1].";\n\n";						for ($i = 0; $i < $num_fields; $i++) 		{			while($row = mysql_fetch_row($result))			{				$return.= 'INSERT INTO '.$table.' VALUES(';				for($j=0; $j<$num_fields; $j++) 				{					$row[$j] = addslashes($row[$j]);					$row[$j] = preg_replace("/\n/","\\n",$row[$j]);					if (isset($row[$j])) { $return.= '"'.$row[$j].'"' ; } else { $return.= '""'; }					if ($j<($num_fields-1)) { $return.= ','; }				}				$return.= ");\n";			}		}		$return.="\n\n\n";		$return.="\n -- Backup of MicroNews Widget";				//Downloading file 		$filename = 'db-kushmicronews-backup-'.time().'.sql';	Header("Content-type: application/octet-stream");	Header("Content-Type: text/x-sql");	Header("Content-Disposition: attachment; filename=$filename");	echo $return;	}?>